import React, { useState, useEffect } from 'react';
import styles from './Hero.module.css';

const Hero = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [windowWidth, setWindowWidth] = useState(0);

  useEffect(() => {
    // Animation trigger
    setIsVisible(true);

    // Window resize handler for dynamic responsiveness
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    // Set initial width
    setWindowWidth(window.innerWidth);

    // Add event listener
    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Dynamic content based on screen size
  const getSubtitleText = () => {
    if (windowWidth < 480) {
      return "Cinematic Soundscapes";
    } else if (windowWidth < 768) {
      return "Gateway to Cinematic Soundscapes";
    }
    return "Your Gateway to Cinematic Soundscapes";
  };

  const handleCTAClick = (e) => {
    e.preventDefault();
    // Add your CTA logic here
    console.log('CTA clicked');
  };

  return (
    <section className={styles.heroSection}>
      <div className={styles.heroGradient} />
      <div className={`${styles.heroContent} ${isVisible ? styles.fadeIn : ''}`}>
        <h1 className={styles.heroTitle}>
          AGNI
        </h1>
        <p className={styles.heroSubtitle}>
          {getSubtitleText()}
        </p>
        <button 
          className={styles.ctaButton}
          onClick={handleCTAClick}
          aria-label="Explore our cinematic soundscapes"
        >
          Explore Now
        </button>
      </div>
    </section>
  );
};

export default Hero;
