import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInstagram, faFacebook, faWhatsapp } from '@fortawesome/free-brands-svg-icons';

const socialLinks = [
  {
    name: "Instagram",
    icon: faInstagram,
    href: "https://www.instagram.com/agiswaraband/"
  },
  {
    name: "Facebook",
    icon: faFacebook,
    href: "https://www.facebook.com/agiswaraband/"
  },
  {
    name: "WhatsApp",
    icon: faWhatsapp,
    href: "https://wa.me/919876543210"
  }
];

const ContactUs = () => {
  return (
    <section className="contact-section full-height bg-light py-5">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <div className="row g-4">
              <div className="col-md-6">
                <div className="contact-info bg-white p-5 rounded-4 shadow-sm">
                  <div className="d-flex align-items-center mb-4">
                    <i className="fa-solid fa-envelope me-3 text-primary" style={{ fontSize: '2.5rem' }} />
                    <a href="mailto:yourband@email.com" className="text-dark text-decoration-none fs-5">
                      yourband@email.com
                    </a>
                  </div>
                  <div className="d-flex align-items-center mb-4">
                    <i className="fa-solid fa-phone me-3 text-primary" style={{ fontSize: '2.5rem' }} />
                    <a href="tel:+911234567890" className="text-dark text-decoration-none fs-5">
                      +91 1234567890
                    </a>
                  </div>
                  <div className="d-flex align-items-center">
                    <i className="fa-solid fa-phone me-3 text-primary" style={{ fontSize: '2.5rem' }} />
                    <a href="tel:+911234567891" className="text-dark text-decoration-none fs-5">
                      +91 1234567891
                    </a>
                  </div>
                </div>
              </div>
              <div className="col-md-6">
                <div className="social-links text-center py-4">
                  <h3 className="mb-4 fs-2">Follow Us</h3>
                  <div className="d-flex justify-content-center gap-5">
                    {socialLinks.map((link, index) => (
                      <a
                        key={index}
                        href={link.href}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-decoration-none text-white fs-1"
                        aria-label={`Visit ${link.name}`}
                      >
                        <FontAwesomeIcon icon={link.icon} />
                      </a>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ContactUs;
