import React, { useState, useEffect } from 'react';

const Events = () => {
  const events = [
    {
      title: "Summer Concert",
      date: "June 15, 2025",
      images: [
        "https://lh3.googleusercontent.com/aida-public/AB6AXuC1d3LuM3T2oFWPiaiefRH6L7uQGGSGl_sGNXZs41EfUjEEzbQkuprQ78dp-hBnkG5ZxwDBSFM4_uh4HPzrR2QMTaSp8dwmbsUlqxb7E13Nobm_Yy2_Oy8N2ZYUrYIcPpVSrQyt_rPlZ7MgK-skGmRI_SnmyEkHLYM1CPcJrqkSNWnIDDUZWBHc1eTh91i_WS0IB1FQNJKx_NqHGfCpTZyQeQcGM--dnzHdCMk6qwHmqT4CIMhEaakHbcinNgUugDyeLM8uPkEO_fI",
        "https://lh3.googleusercontent.com/aida-public/AB6AXuBx3ldw8MlYDyWytEtOMmjEHbWvg8eV8L59I7DqzANsPvhG4bKAEloWlsQNj-84gzKdQtNDe_VXsB2Wta7BDFwhqGoIxjj2z_KbVggXvNB_Y5R6B_t2albvlNRkhWlxHD9d3QNLtBgM9lz_r3kMR_9A6KWmUjunkFMggwrB46XOq4fbykBkVF-A2sFE3Pj_-aO8LCXohejasXbTegl3KPEULt_rLj2gdgsSabvztvoNqgj7ENc4sTFoi8RXCoDBGJsq9ppTg-B2W9c"
      ],
      description: "Join us for an amazing summer concert under the stars. Featuring special guest artists and fireworks display."
    },
    {
      title: "Jazz Festival",
      date: "July 20, 2025",
      images: [
        "https://lh3.googleusercontent.com/aida-public/AB6AXuAHrYgH7gUUXV5G3HNLVkGZe8A-NR7sGeHnsKjrJsnNwFbhUHxUdrwcpqHZCTxzDxJQpHSN5MFEQf0aC_d2myiy-fVlQUx5OL1BeU2c9cZ2bAp6t5TmZrIqzlHQAUUja2a9CHSHNu3did-fkAdECimAcTfrAFVc9GAXitcZSn5FFizVPl1NvXHJuZObuQyTDYpmxFN7r8ulaV8ArljyQtgy0-pgwAGyILnd-9EvnlOEal0rTS0HkVetQIAQ8kS4i1k8TbcSbQd983Y",
        "https://lh3.googleusercontent.com/aida-public/AB6AXuCWPM8WXFVpbTyi-sS4eRO69xJHPZjVGxqqEs2XnE6XZh7VTbXNF_NQDUpF3DOWN_GzVwf-LARM1AeGrmuXNmzr_q_sEGkLmks_B9rZ3klDaiwmME6cnhctl3I8pcoHf1AM2fi2BO1-adV5CJSHxIaF85DVyBjVpRsjvFPGhRuf177oXNrjbM41mDI94h10xL3YBO-H9YfuRKR9K5ybr2Zqpi8AN2cxHUsqQVXIyN07ga5tGbL-7qSUUKpN0onGQgj1knqj_mlUD7k"
      ],
      description: "Experience the magic of jazz with our special festival featuring multiple bands and performances throughout the day."
    },
    {
      title: "New Year's Eve Show",
      date: "December 31, 2025",
      images: [
        "https://lh3.googleusercontent.com/aida-public/AB6AXuBe1kyJEByffh_b8MpM8lHIfwfshoD6AcdOs7NjlxkZeIy4SVOcW6kkrKwMeFc4Qwtjke4QvUxgv_qmPkpFmJp-Mr9rhTMdFs6c8iV4LTfSNzponBlZojljDYVqPR_9KsHNAhyjEM1DQryjBUaypBWDMc9Fv-ISU3IjsGCbpwrvaZneid-nLLgwTz4mXihwR2IxHK_mj6wk2DMW7rd_-NNjBRjLgVButkQBir4LNcdbRDYjjXmbswyzlYJQa88gFSFZ5vKm3XYk6xk",
        "https://lh3.googleusercontent.com/aida-public/AB6AXuD3CsGIhLeKqth5B9G_iSHH_i1tzAcEqz7imXgQpENPh5zF19ZjeDmZdFn85m_vN4Zyns-AKZikldqP5xZzgVz7cIN2EwydMFIe6c3oQmigmzK4KTE4wqLO-rzaSY_me7ttKRs2MD_rn3gCKto1zvyqVTR3EeQT76DCPjslOsmTMYMGvU02v84BA18LRFC1IEwsMYYVdGqI4i6lPIvQQFbAzPwqz9XZPbMgmS8nMQ-MInMCvRrbWqeF2hat0Dx63Y8mbfxfnoL4_zQ"
      ],
      description: "Ring in the new year with our special performance featuring fireworks and midnight countdown."
    }
  ];

  return (
    <div className="events-section">
      <div className="events-container">
        {events.map((event, index) => {
          const [currentIndex, setCurrentIndex] = useState(0);

          useEffect(() => {
            const interval = setInterval(() => {
              setCurrentIndex((prevIndex) => (prevIndex + 1) % event.images.length);
            }, 3000);

            return () => clearInterval(interval);
          }, [event.images.length]);

          return (
            <div key={index} className="event-card">
              <div className="event-carousel">
                {event.images.map((image, imgIndex) => (
                  <div 
                    key={imgIndex} 
                    className={`event-image ${currentIndex === imgIndex ? 'active' : ''}`} 
                    style={{ backgroundImage: `url(${image})` }}
                  ></div>
                ))}
              </div>
              <div className="event-info">
                <h3 className="event-title">{event.title}</h3>
                <p className="event-date">{event.date}</p>
                <p className="event-description">{event.description}</p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Events;
