import React from 'react';

const Hero = () => {
  return (
    <section className="hero full-height position-relative">
      <div className="hero-gradient position-absolute top-0 left-0 w-100 h-100"></div>
      <div className="container h-100">
        <div className="row h-100 align-items-center">
          <div className="col-12 text-center">
            <h1 className="display-1 fw-bold text-white mb-4">Agni Swara</h1>
            <p className="lead text-white mb-5">Your Gateway to Cinematic Soundscapes</p>
            <a href="#events" className="btn btn-primary btn-lg px-5 py-3">Upcoming Events</a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
