import React from 'react';

const AboutUs = () => {
  return (
    <section className="about-us-section">
      {/* Hero Section */}
      <div className="about-hero">
        <div className="about-overlay">
          <p className="section-subtitle">
            Harmonic Motion - Your Gateway to Cinematic Soundscapes
          </p>
        </div>
      </div>

      {/* Band Story */}
      <div className="about-content">
        <div className="band-story">
          <h3 className="section-heading">Our Story</h3>
          <p className="section-text">
            Founded in 2015, Harmonic Motion has been captivating audiences with our unique blend of film music and live performance. Our journey began with a shared passion for cinematic soundtracks and a vision to bring these iconic scores to life on stage.
          </p>
          <p className="section-text">
            Over the years, we've grown from a small ensemble to a full band, performing at major venues and festivals across the country. Our repertoire spans classic film scores to modern cinematic masterpieces, all delivered with our signature style and energy.
          </p>
        </div>


      </div>
    </section>
  );
};

export default AboutUs;
