import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import styles from './Navbar.module.css';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      setScrolled(currentScrollY > 0);
      setLastScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  return (
    <nav className={`${styles.navbar} ${scrolled ? styles.scrolled : ''}`}>
      <div className={styles.navbarContent}>
        <a className={styles.navbarBrand} href="#home" aria-label="Agni Swara Home">
          <img src="/Logo.jpg" alt="Agni Swara Logo" width="80" height="80" className={styles.navbarBrandImg} />
        </a>
        <button
          className={styles.menuToggle}
          aria-label={isMenuOpen ? 'Close menu' : 'Open menu'}
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          <FontAwesomeIcon icon={isMenuOpen ? faTimes : faBars} />
        </button>
        <ul className={`${styles.navLinks} ${isMenuOpen ? styles.showMenu : ''}`}>
          <li className={styles.navItem}>
            <a className={styles.navLink} href="#home">Home</a>
          </li>
          <li className={styles.navItem}>
            <a className={styles.navLink} href="#band">Band</a>
          </li>
          <li className={styles.navItem}>
            <a className={styles.navLink} href="#events">Events</a>
          </li>
          <li className={styles.navItem}>
            <a className={styles.navLink} href="#about">About</a>
          </li>
          <li className={styles.navItem}>
            <a className={styles.navLink} href="#contact">Contact</a>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
